window.addEventListener("load",()=>{"use strict";const e=document.querySelector("#languages"),t=document.querySelector(".lang-box"),l=document.querySelectorAll(".flag-button"),n=document.querySelector("body");e.addEventListener("click",()=>{t.classList.toggle("languages");for(let e=0;e<l.length;e++)l[e].classList.toggle("flag")}),n.addEventListener("click",()=>{!0===e&&t.classList.toggle("languages")});const s=document.querySelector("#white-bars"),c=document.querySelector(".list-js");s.addEventListener("click",()=>{innerWidth<650&&c.classList.toggle("list_container")}),innerWidth>650&&c.classList.add("list_container")});